import time
for record in records:
  try:
    newRecord = sdcFunctions.createRecord(record.sourceId + str(time.time()))
    newRecord.value = {}
    newRecord.attributes['binlogfilename'] = record.value['BinLogFilename']
    newRecord.attributes['type'] = str(record.value['Type'])
    newRecord.attributes['table'] = str(record.value['Table'])
    newRecord.attributes['binlogpos'] = str(record.value['BinLogPosition'])
    newRecord.attributes['database'] = str(record.value['Database'])
    newRecord.attributes['timestamp'] = str(record.value['Timestamp'])
    newRecord.attributes['offset'] = record.value['Offset']

    if record.value['Type'] == 'INSERT':
      newRecord.attributes['sdc.operation.type'] = '1'
    if record.value['Type'] == 'DELETE':
      newRecord.attributes['sdc.operation.type'] = '2'
    if record.value['Type'] == 'UPDATE':
      newRecord.attributes['sdc.operation.type'] = '3'

    data = record.value['Data']
    for key in data:
      newRecord.value[key] = data[key]

    output.write(newRecord)

  except Exception as e:
    # Send record to error
    error.write(record, str(e))

